mapper.insert:
	insert into dubbing (
		dubbingnum,
		title,
		content,
    	useremail,
    	url,
    	voiceFile,
    	starttime,
    	endtime
	) values(
		DUBBING_SEQ.nextval
		, #{title}
		, #{content}
		, #{useremail}
		, #{url}
		, #{voiceFile}
		, #{starttime}
		, #{endtime}
	)

mapper.delete:
delete from dubbingreply
where useremail=#{useremail}
and dubbingnum=#{idnum}

mapper.insert:
insert into blacklist (
	useremail,
	whichboard,
	replynum
) values(
	  #{useremail}
	, #{whichboard}
	, #{replynum}
)

insert into dubbingreply (
	replynum,
	dubbingnum,
	useremail,
	content
) values(
	dubbing_reply_seq.nextval		
	, #{idnum}
	, #{useremail}
	, #{content}
)
