mapper.delete:
delete from
   wishlist
where
   title = #{title}
and
   useremail = #{useremail}

mapper.insert:
insert into wishlist(
   wishtable   
   , useremail
   , identificationnum
   , title
   , url
)values(
   #{wishtable}   
   , #{useremail}
   , #{identificationnum}
   , #{title}
   , #{url}
)
