mapper.insert:
insert into investigation (
	investigationnum
	, url
	, title
	, content
	, useremail
)values(
	INVESTIGATION_SEQ.nextval
	, #{url}
	, #{title}
	, #{content
	, jdbcType=VARCHAR}
	, #{useremail}
)

insert into investigationreply (
	replynum
	, investigationnum
	, useremail
	, content
)values(
	INVESTIGATION_REPLY_SEQ.nextval
	, #{idnum}
	, #{useremail}
	, #{content}
)

insert into InvestigationSubtitle (
	subtitlenum
	, subtitleName
	, originalFile
	, savedFile
	, useremail
	, investigationnum
)values(
	INVESTIGATION_SUBTITLE_SEQ.nextval
	, #{subtitleName}
	, #{originalFile}
	, #{savedFile}
	, #{useremail}
	, #{investigationnum}
)
