mapper.insert:
insert into wishlist(
	wishnum
	, videoNum
	, useremail
	, title
	, url		
	, regDate	
)values(
	wish_list_seq.nextval
	, #{videoNum}
	, #{useremail}
	, #{title}
	, to_char(regDate, 'YYYY-MM-DD') regDate
)

